  // eslint-disable-next-line class-methods-use-this
  destroyCube(cube) {
    // eslint-disable-next-line no-param-reassign
    cube.state = 'nothing';
  }

  generateHints() {
    const hints = [];
    for (let x = 0; x < 10; x += 1) {
      for (let y = 0; y < 10; y += 1) {
        for (let z = 0; z < 10; z += 1) {
          hints[x * 100 + y * 10 + z] = this.generateHint(x, y, z);
        }
      }
    }

    return hints;
  }

  generateHint(x0, y0, z0) {
    let xCount = 0;
    let yCount = 0;
    let zCount = 0;
    for (let x = 0; x < 10; x += 1) {
      if (this.cubes[x * 100 + y0 * 10 + z0] >= 0x30) {
        xCount += 1;
      }
    }

    for (let y = 0; y < 10; y += 1) {
      if (this.cubes[x0 * 100 + y * 10 + z0] >= 0x30) {
        yCount += 1;
      }
    }

    for (let z = 0; z < 10; z += 1) {
      if (this.cubes[x0 * 100 + y0 * 10 + z] >= 0x30) {
        zCount += 1;
      }
    }

    return {
      x: xCount,
      y: yCount,
      // z: zCount,
    };
  }

  isSolved() {
    return this.cubes.every((c) => c.state !== 'empty' && c.state !== 'paintedEmpty');
  }
