  // eslint-disable-next-line class-methods-use-this
  destroyCube(cube) {
    // eslint-disable-next-line no-param-reassign
    cube.state = 'nothing';
  }

  generateHints() {
    const hints = {
      x: [],
      y: [],
      z: [],
    };

    for (let y = 0; y < 10; y += 1) {
      hints.x[y] = [];
      for (let z = 0; z < 10; z += 1) {
        hints.x[y][z] = this.generateLineHint(null, y, z, 0);
      }
    }

    for (let x = 0; x < 10; x += 1) {
      hints.y[x] = [];
      for (let z = 0; z < 10; z += 1) {
        hints.y[x][z] = this.generateLineHint(x, null, z, 1);
      }
    }

    for (let x = 0; x < 10; x += 1) {
      hints.z[x] = [];
      for (let y = 0; y < 10; y += 1) {
        hints.z[x][y] = this.generateLineHint(x, y, null, 2);
      }
    }

    return hints;
  }

  generateLineHint(x0, y0, z0, axis) {
    let previous;
    let count = 0;
    let spaces = 0;

    // todo this can look much better using a 3 elements array [100, 10, 1]
    for (let i = 0; i < 10; i += 1) {
      const cube = axis === 0
        ? this.cubes[i * 100 + y0 * 10 + z0]
        : (axis === 1
          ? this.cubes[x0 * 100 + i * 10 + z0]
          : this.cubes[x0 * 100 + y0 * 10 + i]);

      // Count the part elements
      if (cube >= 0x30) {
        count += 1;
      // Count the spaces (only count each space once)
      } else if (previous >= 0x30) {
        spaces += 1;
      }
      previous = cube;
    }

    // If we finished with a space, remove the last space
    if (previous < 0x30) {
      spaces -= 1;
    }

    return { count, spaces };
  }

  isSolved() {
    return this.cubes.every((c) => c.state !== 'empty' && c.state !== 'paintedEmpty');
  }

